        if(i >= 2) for(int cut = 1; cut <= i / 2; cut++)
            money = max(money, dp[cut][j] + dp[i - cut][j]);
        if(j >= 2) for(int cut = 1; cut <= j / 2; cut++)
            money = max(money, dp[i][cut] + dp[i][j - cut]);
        dp[i][j] = money;
