#define ll long long

class Solution {
public:

	// storing given {i, j} ans in map to fetch it out quickly
    ll solve(int n, int m, map<pair<int, int>, ll> &hsh, vector<vector<ll>> &dp){
        if(dp[n][m] != -1) return dp[n][m];
        ll ans = hsh.find({n, m}) == hsh.end() ? 0 : hsh[{n, m}];
        // Why taking individual rows and cols?
        // Becuase in each and every individual call for particular i we get all col combination in it's function call in next for loop of column. So by this we get each combination of i and j
        for(ll i = 1; i < n; i++){
            ans = max(ans, solve(i, m, hsh, dp) + solve(n - i, m, hsh, dp));
        }
        for(ll j = 1; j < m; j++){
            ans = max(ans, solve(n, j, hsh, dp) + solve(n, m - j, hsh, dp));
        }
        return dp[n][m] = ans;
    }

    long long sellingWood(int m, int n, vector<vector<int>>& prices) {
        map<pair<int, int>, ll> hsh;
        vector<vector<ll>> dp(m + 1, vector<ll>(n + 1, -1));
        for(auto i : prices) hsh[{i[0], i[1]}] = i[2];
        return solve(m, n, hsh, dp);
    }
};
