class Solution {
    public long sellingWood(int m, int n, int[][] prices) {
        Long[][] cache = new Long[m+1][n+1];
        // maxPriceNoCut is best price if no cut for size m * n
        int[][] maxPriceNoCut = getMaxPriceNoCut(m, n, prices);	
        return helper(m, n, maxPriceNoCut, cache);
    }
	// Time O(mn (m+n))
    private long helper(int m, int n, int[][] maxPriceNoCut, Long[][] cache){
        if(cache[m][n] != null){
            return cache[m][n];
        }
        // Max price if not cut
        long max = maxPriceNoCut[m][n]; 
        // Try horizontal cut
        for(int i=1;i<m;i++){
            long piece1 = helper(i, n, maxPriceNoCut, cache);
            long piece2 = helper(m-i, n, maxPriceNoCut, cache);
            max = Math.max(max, piece1 + piece2);
        }
        // Try vertical  cut
        for(int i=1;i<n;i++){
            long piece1 = helper(m, i, maxPriceNoCut, cache);
            long piece2 = helper(m, n-i, maxPriceNoCut, cache);
            max = Math.max(max, piece1 + piece2);
        }
        return cache[m][n] = max;
    }
	// Time O(mn)
    private int[][] getMaxPriceNoCut(int m, int n, int[][] prices){
        int[][] maxPriceNoCut = new int[m+1][n+1];
        for(int[] p : prices){
            maxPriceNoCut[p[0]][p[1]] = p[2];
        }
        for(int i=1;i<=m;i++){
            for(int j=1;j<=n;j++){
                maxPriceNoCut[i][j] = Math.max(maxPriceNoCut[i][j], maxPriceNoCut[i-1][j]);
                maxPriceNoCut[i][j] = Math.max(maxPriceNoCut[i][j], maxPriceNoCut[i][j-1]);
            }
        }
        return maxPriceNoCut;
    }
}
