class Solution {
public:
    using ll = long long;
    long long sellingWood(int m, int n, vector<vector<int>>& prices) {
        unordered_map<int, vector<int>> dict_row;//key: row, value: price idx
        unordered_map<int, vector<int>> dict_col;//key: col, value: price idx
        for(int i=0;i<prices.size();++i){
            const auto& p = prices.at(i);
            int h = p[0];
            int w = p[1];
            dict_row[h].push_back(i);
            dict_col[w].push_back(i);
        }
        vector<vector<ll>> dp(m + 1, vector<ll>(n + 1, 0));
        for(int i=0;i<m;++i){
            dp[i][0] = 0;
        }
        for(int j=0;j<n;++j){
            dp[0][j] = 0;
        }
        for(int i=0;i+1<=m;++i){
            for(int j=0;j+1<=n;++j){
                // dp[i+1][j]
                for(int k=1;k<=(i+1)/2;++k){
                    dp[i+1][j] = max(dp[i+1][j], dp[k][j] + dp[i+1-k][j]);
                }
                if(dict_row.count(i + 1) > 0){
                    for(auto idx : dict_row[i+1]){
                        ll h = i + 1;
                        ll w = prices[idx][1];
                        ll cash = prices[idx][2];
                        for(ll k=1;k * w <= j;++k){
                            dp[i+1][j] = max(dp[i+1][j], cash * k + dp[i+1][j - k * w]);
                        }
                    }
                }

                //dp[i][j+1]
                for(int k=1;k<=(j+1)/2;++k){
                    dp[i][j+1] = max(dp[i][j+1], dp[i][k] + dp[i][j+1-k]);
                }
                if(dict_col.count(j + 1) > 0){
                    for(auto idx : dict_col[j+1]){
                        ll h = prices[idx][0];
                        ll w = j + 1;
                        ll cash = prices[idx][2];
                        for(ll k=1;k * h <= i;++k){
                            dp[i][j+1] = max(dp[i][j+1], cash * k + dp[i - k * h][j+1]);
                        }
                    }
                }
            }
        }
        //dp[m][n]
        for(int k=1;k<=m/2;++k){
            dp[m][n] = max(dp[m][n], dp[k][n] + dp[m-k][n]);
        }
          if(dict_row.count(m) > 0){
                for(auto idx : dict_row[m]){
                    ll h = m;
                    ll w = prices[idx][1];
                    ll cash = prices[idx][2];
                    for(ll k=1;k * w <= n;++k){
                        dp[m][n] = max(dp[m][n], cash * k + dp[m][n - k * w]);
                    }
                }
            }

        for(int k=1;k<=n/2;++k){
            dp[m][n] = max(dp[m][n], dp[m][k] + dp[m][n-k]);
        }
        if(dict_col.count(n) > 0){
            for(auto idx : dict_col[n]){
                ll h = prices[idx][0];
                ll w = n;
                ll cash = prices[idx][2];
                for(ll k=1;k * h <= m;++k){
                    dp[m][n] = max(dp[m][n], cash * k + dp[m - k * h][n]);
                }
            }
        }

        return dp[m][n];
    }
};
