/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:
    vector<vector<int>> spiralMatrix(int n, int m, ListNode* head) {
        vector<vector<int>> ans(n, vector<int> (m, -1));
        ListNode* tmp = head;
        int t = 0, s = 0;
        int b = n-1, a = m-1;
        while(1)
        {
            for(int i=s;i<=a;i++)
            {
                ans[t][i] = tmp->val;
                tmp = tmp->next;
                if(tmp == NULL)
                    break;
            }
            t++;
            if(tmp == NULL)
                break;

            for(int i = t;i<=b;i++)
            {
                ans[i][a] = tmp->val;
                tmp = tmp->next;
                if(tmp == NULL)
                    break;
            }
            a--;
            if(tmp == NULL)
                break;

            for(int i = a;i>=s;i--)
            {
                ans[b][i] = tmp->val;
                tmp = tmp->next;
                if(tmp == NULL)
                    break;
            }
            b--;
            if(tmp == NULL)
                break;

            for(int i = b;i>=t;i--)
            {
                ans[i][s] = tmp->val;
                tmp = tmp->next;
                if(tmp == NULL)
                    break;
            }
            s++;
            if(tmp == NULL)
                break;
        }
        return ans;
    }
};

