    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> ans(m,vector<int>(n,-1));
        int i = 0, j = 0;
	    bool flag=false;
        while(i <= (m-1)/2 && j <= (n-1)/2) {
            for(int y=j; y<n-j; y++){
                if(head==NULL){
                    flag=true;
                    break;
                }
                ans[i][y]=head->val;
                head=head->next;
            }


            for(int x=i+1; x<m-i; x++){
