        current = head
        direction = 1
        i, j = 0, -1

        while current:
            for _ in range(n):
                if current:
                    j += direction
                    matrix[i][j] = current.val
                    current = current.next

            m -= 1

            for _ in range(m):
                if current:
                    i += direction
                    matrix[i][j] = current.val
                    current = current.next
            n -= 1

            direction *= -1

        return matrix

