class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        int top = 0, left = 0, right = n - 1, down = m - 1;
        int dir = 0;
        vector<vector<int>> answer(m, vector<int>(n, -1));
        while(top <= down && left <= right && head) {
            if(dir == 0) {
                for(int col = top; col <= right; col++) {
                    if(!head) return answer;
                    int val = head -> val;
                    answer[top][col] = val;
                    head = head -> next;
                }
                top++;
            }else if(dir == 1) {
                for(int row = top; row <= down; row++) {
                    if(!head) return answer;
                    int val = head -> val;
                    answer[row][right] = val;
                    head = head -> next;
                }
                right--;
            }else if(dir == 2) {
                for(int col = right; col >= left; col--) {
                    if(!head) return answer;
                    int val = head -> val;
                    answer[down][col] = val;
                    head = head -> next;
                }
                down--;
            }else {
                for(int row = down; row >= top; row--) {
                    if(!head) return answer;
                    int val = head -> val;
                    answer[row][left] = val;
                    head = head -> next;
                }
                left++;
            }
            dir = (++dir) % 4;
        }
        return answer;
    }
