var spiralMatrix = function(m, n, head) {
    let matrix = []
    for (let i = 0; i < m; i++) {
        let arr = [];
        for (let j = 0; j < n; j++) arr.push(-1)
        matrix.push(arr)
    }
    let list = []
    let li = head
    while (li != null) {
        list.push(li.val)
        li = li.next;
    }
    let index = 0
    let rowBoundaries = 0
    let colBoundaries = 0
    let row = 0
    let col = -1
    while (true) {
        while (col<matrix[0].length-colBoundaries-1 && index<list.length) {
            col++
            matrix[row][col] = list[index]
            index++
        }
        while (row<matrix.length-rowBoundaries-1 && index<list.length) {
            row++
            matrix[row][col] = list[index]
            index++
        }
        while (col>colBoundaries && index<list.length) {
            col--
            matrix[row][col] = list[index]
            index++
        }
        rowBoundaries++
        colBoundaries++
        while (row>rowBoundaries && index<list.length) {
            row--
            matrix[row][col] = list[index]
            index++
        }
        if (index>=list.length) break
    }
    return matrix
};
