class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {
        int[][] ans = new int[m][n];
        int rowStart = 0;
        int rowEnd = m - 1;
        int colStart = 0;
        int colEnd = n - 1;
        ListNode temp = head;

        while(rowStart <= rowEnd && colStart <= colEnd) {
            // top -->
            for(int j = colStart; j <= colEnd; j++) {
                if(temp == null) {
                    ans[rowStart][j] = -1;
                } else {
                    ans[rowStart][j] = temp.val;
                    temp = temp.next;
                }
            }

            // Right -->
            for(int i = rowStart + 1; i <= rowEnd; i++) {
                if(temp == null) {
                    ans[i][colEnd] = -1;
                } else {
                    ans[i][colEnd] = temp.val;
                    temp = temp.next;
                }
            }

            //Bottom -->
            for(int j = colEnd - 1; j >= colStart; j--) {
                if(rowStart == rowEnd) break; // V.V.I (base condition)
                if(temp == null) {
                    ans[rowEnd][j] = -1;
                } else {
                    ans[rowEnd][j] = temp.val;
                    temp = temp.next;
                }
            }

            // Left -->
            for(int i = rowEnd - 1; i >= rowStart + 1; i--) {
                if(colStart == colEnd) break; // V.V.I (base condition)
                if(temp == null) {
                    ans[i][colStart] = -1;
                } else {
                    ans[i][colStart] = temp.val;
                    temp = temp.next;
                }
            }

            rowStart++;
            rowEnd--;
            colStart++;
            colEnd--;
        }

        return ans;
    }
}
