/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {
        //arr to store the  answer
        int[][] arr = new int[m][n];
        //Fill every row of the array with -1
        for(int[] x : arr) {
             Arrays.fill(x,-1);
        }
        //We have to traverse spirally to get the required matrix
        //top-left to right
        //top-right to bottom
        //bottom-right to left
        //bottom-left to top
        //We have to traverse according to above mentioned paths from head of the linkedlist
        int top = 0, bottom = m-1;
        int left = 0, right = n-1;
        //traverse the list until your head didn't face the null
        while(head!=null){
            //This for-loop stores elements from top.left-right
            for(int i = top;i<=right && head!=null ;i++){
                arr[top][i] = head.val;
                head = head.next;
            }
            //Increment the top value to avoid duplicates
            top++;
            //This for-loop stores values from top.right to bottom
            for(int i = top;i<=bottom && head!=null;i++){
                arr[i][right] = head.val;
                head = head.next;
            }
            //Decrement the right value to avoid duplicates
            right--;
            //This for-loop stores elements from bottom.right to left
            for(int i = right;i>=left && head!=null;i--){
                arr[bottom][i] = head.val;
                head = head.next;
            }
            //Decrement the bottom value to avoid duplicates
            bottom--;
            //This for-loop stores elements from bottom.left to top
            for(int i = bottom;i>=top && head!=null;i--){
                arr[i][left] = head.val;
                head = head.next;
            }
            //Increment the left value to avoid duplicates
            left++;

        }
        return arr;
    }
}






















