class Solution {
public:
    // curr: direction 0=right, 1=down, 2=left, 3=top
    // if going in a direction and encounter end of matrix or matrix[next_i][next_j]!=-1
    // then switch the direction, and move to new matrix[next_i][next_j] according to the given direction
    // if this element is also not -1, then end the traversal
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>>ans(m,vector<int>(n,-1));
        int curr=0,i=0,j=0; 
        while(head){
            ans[i][j]=head->val;
            bool check=false;
            switch(curr){
                case 0:{
                    if(j+1>=n || ans[i][j+1]!=-1){
                        curr++;
                        i++;
                    }else j++;
                    break;
                }
                case 1:{
                    if(i+1>=m || ans[i+1][j]!=-1){
                        curr++;
                        j--;
                    }else i++;
                    break;
                }
                case 2:{
                    if(j-1<0 || ans[i][j-1]!=-1){
                        curr++;
                        i--;
                    }else j--;
                    break;
                }
                case 3:{
                    if(i-1<0 || ans[i-1][j]!=-1){
                        curr=0;
                        j++;
                    }else i--;
                    break;
                }
            }
            if(i<0 || i>=m || j<0 || j>=n || ans[i][j]!=-1)
                break;
            head=head->next;
        }
        return ans;
    }
};
