   vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
    vector<vector<int>>vec(m,vector<int>(n));
    int s =0;
    int left =0;
    int right = n-1;
    int top = 0;
    int bottom = m-1;
    ListNode *cursor = head;
    while (s < m*n){
        for(int i = left; i<=right && s<m*n; i++){
            if(cursor == nullptr) {
                vec[top][i] = -1;
                s++;
            }else{
                vec[top][i] = cursor->val;
                cursor = cursor->next;
                s++;
            }
