class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {
        int[][] res = new int[m][n];
        int r = 0, c = 0, d = 0, x = 0, y = 0;
        int[] dx = new int[] {0, 1, 0, -1};        
        int[] dy = new int[] {1, 0, -1, 0};
        int[] border = new int[] {n - 1, m - 1, 0, 1};
        while (c < m * n) {
