class Solution:
    def spiralMatrix(self, m: int, n: int, head: Optional[ListNode]) -> List[List[int]]:

        result = [[-1]*n for i in range(m)]

        top,bottom,left,right=0,m-1,0,n-1

        direction = 0

        while head and left<=right and top<=bottom:

            if direction==0:
                for i in range(left,right+1):
                    if head:
                        result[top][i] = head.val
                        head = head.next
                    else:
                        return result
                top += 1

            elif direction==1:
                for i in range(top,bottom+1):
                    if head:
                        result[i][right] = head.val
                        head = head.next
                    else:
                        return result
                right -= 1

            elif direction==2:
                for i in range(right,left-1,-1):
                    if head:
                        result[bottom][i] = head.val
                        head = head.next
                    else:
                        return result
                bottom -= 1

            else:
                for i in range(bottom,top-1,-1):
                    if head:
                        result[i][left] = head.val
                        head = head.next
                    else:
                        return result
                left += 1

            direction = (direction + 1)%4

        return result
