                        result[bottom][i] = head.val
                        head = head.next
                    else:
                        return result
                bottom -= 1

            else:
                for i in range(bottom,top-1,-1):
                    if head:
                        result[i][left] = head.val
                        head = head.next
                    else:
                        return result
                left += 1

            direction = (direction + 1)%4

        return result
