        //vector<vector<int>>ret(m,vector<int>(n,-1));
        ret.resize(m,vector<int>(n,-1));
        recur(head);
        return ret;
    }
    void recur(ListNode*head){
        if(!head)
            return;
        if(xs>n||ys>m)
            return;
        int i,j;
        i=xs;
        j=ys;
        for(;i<n&&head;i++){
            ret[j][i]=head->val;
            head=head->next;
        }
        ++ys;
        i-=1;
        j+=1;
        for(;j<m&&head;j++){
            ret[j][i]=head->val;
            head=head->next;
        }
        --n;
        j-=1;
        i-=1;
        for(;i>=xs&&head;i--){
            ret[j][i]=head->val;
            head=head->next;
        }
        --m;
        i+=1;
        j-=1;
        for(;j>=ys&&head;j--){
            ret[j][i]=head->val;
            head=head->next;
        }
        ++xs;
        recur(head);


    }
};

