public int[][] spiralMatrix(int m, int n, ListNode head) {
        int[][] mat = new int[m][n];
        int rowStart = 0;
        int rowEnd = m-1;
        int colStart = 0;
        int colEnd = n-1;
        while(rowStart<=rowEnd && colStart<=colEnd) {
            //right
            for(int i=colStart; i<=colEnd; i++) {
                if(head == null) {
                    mat[rowStart][i] = -1;
                } else {
                    mat[rowStart][i] = head.val;
                    head = head.next;
                }
            }
            rowStart++;
            //down
            for(int i=rowStart; i<=rowEnd; i++) {
                if(head == null) {
                    mat[i][colEnd] = -1;
                } else {
                    mat[i][colEnd] = head.val;
                    head = head.next;
                }
            }
            colEnd--;
            //left
            if(rowStart <= rowEnd) {
                for(int i=colEnd; i>=colStart; i--) {
                    if(head == null) {
                        mat[rowEnd][i] = -1;
                    } else {
                        mat[rowEnd][i] = head.val;
                        head = head.next;
                    }
                }
            }
            rowEnd--;
            //up
            if(colStart <= colEnd) {
                for(int i=rowEnd; i>=rowStart; i--) {
                    if(head == null) {
                        mat[i][colStart] = -1;
                    } else {
                        mat[i][colStart] = head.val;
                        head = head.next;
                    }
                }
            }
            colStart++;
        }
        return mat;
    }
