            return 'w';
        else if (dir == 'e')
            return 's';
        else 
            return 'n';
    }
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> spiralMatrix (m, vector<int> (n,-2));
        if(m==1 && n==1){
            if(!head){
                spiralMatrix[0][0] = -1;
                return spiralMatrix;
            }
            spiralMatrix[0][0] = head->val;
            return spiralMatrix;

        }

        int matLength = m*n;
        int i=0,j=0;
        int l = 0;
        ListNode* tempHead = head;
        char direction = 'e';
        while(matLength--){
            if(tempHead){
                spiralMatrix[i][j] = tempHead->val;
                tempHead = tempHead->next;
            }
            else{
                spiralMatrix[i][j] = -1;
            }

            bool canGoStraight = goStraight(spiralMatrix,direction,i,j);
            if(canGoStraight)
                continue;

            direction = takeRight(direction);
            goStraight(spiralMatrix,direction,i,j);             
        }
        return spiralMatrix;

    }
};
