/**
 * Definition for singly-linked list.
 * struct ListNode {
 *     int val;
 *     ListNode *next;
 *     ListNode() : val(0), next(nullptr) {}
 *     ListNode(int x) : val(x), next(nullptr) {}
 *     ListNode(int x, ListNode *next) : val(x), next(next) {}
 * };
 */
class Solution {
public:

    // Simple solution.
    // Same as Spiral Matrix I. Using temp to iterate over the linked list.

    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        swap(n, m);
        ListNode* temp = head; // 
        vector<vector<int>> ans(n, vector<int> (m, -1));
        int r=0, c=0;
        while(r<n and c<m and temp) {
            for(int i=c;i<m;i++) {
                if(temp) {
                    ans[r][i] = temp->val;        
                    temp = temp->next;
                }
            }
            r++;
            for(int i=r;i<n;i++) {
                if(temp) {
                    ans[i][m-1] = temp->val;
                    temp = temp->next;
                }
            }
            m--;
            if(r < n) {
                for(int i=m-1;i>=c;i--){
                    if(temp) {
                        ans[n-1][i] = temp->val;
                        temp = temp->next;
                    }
                }
                n--;
            }
            if(c < m) {
                for(int i=n-1;i>=r;i--){
                    if(temp) {
                        ans[i][c] = temp->val;
                        temp = temp->next;
                    }
                }
                c++;
            }
        }
        return ans;
    }
};
