class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {
        int [][]arr=new int[m][n];
        int startrow=0;
        int endrow=m-1;
        int startcol=0;
        int endcol=n-1;
        while(startrow<=endrow && startcol<=endcol)
        {
            // for top
            for(int i=startcol;i<=endcol;i++)
            {
                if(head!=null)
                {
                    arr[startrow][i]=head.val;
                    head=head.next;
                }
