        w = 0
        top , left = 0 , 0
        bot , right = len(matrix) - 1 , len(matrix[0]) - 1
        while(top <= bot and left <= right):
            for i in range(top,right+1):
                if w < len(val):
                    matrix[top][i] = val[w]
                    w += 1
                else:
                    continue
            top += 1

