//FOLLOW UP OF SPIRAL MATRIX 2 
// INDEX ++ KARNE KE BAJAYE LL SE VALUE DAALKE AAGE BADA
//also tmep=NULL TOH ELEMENTS DAALKE HO GAYA HENCE RETURN ARR
class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>> ans(m,vector<int>(n,-1));
        //4 variables lagenge 
        //top
        int top=0;
        int bottom=m-1;
        int left=0;
        int right=n-1;
        //temp bana
        ListNode *temp=head;
      while(top<=bottom and left<=right)
      {
          //-------->
          //for the 1st row
          //1st row fix hai and col is changing
          //chal raha from left to right
          for(int j=left;j<=right;j++)
          {     
              if(temp==NULL) return ans;
              ans[top][j]=temp->val;
              temp=temp->next;
          }
          //first row ie is 0 bhar gaya
          //now second row ir 1 st me jana
          top++;


          //for the last column
          //col fix row changes
          //top se bottom chalega loop
          for(int i=top;i<=bottom;i++)
          {
              if(temp==NULL) return ans;
              ans[i][right]=temp->val;
              temp=temp->next;
          }
          //last column fill ho gaya 
          //ab last column ie 3 se aab 2 me aaye
          right--;

          //<---------------- hence decrement
          //for last row
          //row same hai col changes
          //for chalega from right se left

          for(int j=right;j>=left;j--)
          {
              if(temp==NULL) return ans;
              ans[bottom][j]=temp->val;
              temp=temp->next;
          }
          //last row bhar gaya 
          // ab 2 row se 1 row me jana
          bottom--;

          //upar jana
          //first col same and row changes
          //for chalega from bottom to top
          for(int i=bottom;i>=top;i--)
          {
              if(temp==NULL) return ans;
              ans[i][left]=temp->val;
              temp=temp->next;
          }
        //values bharne ke baad
        //abb --> jayega ie left++
          left++;


      }
      return ans;

    }
};
