/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution
 {
    public int[][] spiralMatrix(int m, int n, ListNode head)
     {

         int row=0;
        int nrow=m-1;
        int col=0;
        int ncol=n-1;
        int size=n*m;
        int c=0;

        int[][] arr=new int[m][n];

        for(int i=0;i<m;i++)
        {
            for(int j=0;j<n;j++)
                arr[i][j]=-1;
        }

        while(head != null){

            for(int i=row;head != null && i<=ncol;i++)
            {
                arr[row][i]=head.val;
                c+=1;
                head=head.next;
            }
            row+=1;

            for(int i=row;head != null && i<=nrow;i++)
            {
                arr[i][ncol]=head.val;
                c+=1;
                head=head.next;
            }
            ncol-=1;

            for(int i=ncol;head != null && i>=col;i--){
                arr[nrow][i]=head.val;
                c+=1;
                head=head.next;
            }
            nrow-=1;

            for(int i=nrow;head != null && i>=row;i--){
                arr[i][col]=head.val;
                c+=1;
                head=head.next;
            }
            col+=1;

        }

        return arr;

    }
}
