class Solution {
public:
    vector<vector<int>> spiralMatrix(int m, int n, ListNode* head) {
        vector<vector<int>>v(m,vector<int>(n,-1));
        int si=0,sm=1,ei=n,em=m;
        while(head)
        {
            for(int i=si;i<ei;i++)
            {
                if(!head)return v;
                v[sm-1][i]=head->val;
                head=head->next;
            }
            for(int i=sm;i<em;i++)
            {
                if(!head)return v;
                v[i][ei-1]=head->val;
                head=head->next;
            }
            for(int i=ei-2;i>=si;i--)
            {
                if(!head)return v;
                v[em-1][i]=head->val;
                head=head->next;
            }
            for(int i=em-2;i>=sm;i--)
            {
                if(!head)return v;
                v[i][si]=head->val;
                head=head->next;
            }
            si++;
            ei--;
            sm++;
            em--;
        }
        return v;
    }
};
```

