    def spiralMatrix(self, m: int, n: int, head: Optional[ListNode]) -> List[List[int]]:
        res = [[-1 for i in range(n)] for j in range(m)]
        LEFT, TOP = 0, 0
        RIGHT, BOT = n, m
        while head:
            for i in range(LEFT, RIGHT):
                if head:
                    res[TOP][i] = head.val
                    head = head.next
                else:
                    return res

            TOP += 1

            for j in range(TOP, BOT):
