/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     public int val;
 *     public ListNode next;
 *     public ListNode(int val=0, ListNode next=null) {
 *         this.val = val;
 *         this.next = next;
 *     }
 * }
 */
public class Solution {
    bool IsHeaderNull = false;
    public int[][] SpiralMatrix(int m, int n, ListNode head) {
        int[][] res = new int[m][];

        for(int i = 0 ; i < m;i++){
            res[i] = new int[n];
            for(int j = 0; j < n ;j++){
                res[i][j] = -1;
            }
        }

        DFS(res,0,0,head,'r');

        return res;
    }

    void DFS(int[][] res,int x, int y, ListNode head, char dir ){
        if(x < 0 || y < 0 || y >= res.Length || x >= res[0].Length || head == null || res[y][x] != -1 || IsHeaderNull){
            if(head == null){
                IsHeaderNull = true;
            }
            return;
        }

        res[y][x] = head.val;
        if(dir == 'u'){
            DFS(res,x,y-1,head.next,'u');
        }
        DFS(res,x+1,y,head.next,'r');
        DFS(res,x,y+1,head.next,'d');
        DFS(res,x-1,y,head.next,'l');
        DFS(res,x,y-1,head.next,'u');
    }
}
```

