/**
 * Definition for singly-linked list.
 * public class ListNode {
 *     int val;
 *     ListNode next;
 *     ListNode() {}
 *     ListNode(int val) { this.val = val; }
 *     ListNode(int val, ListNode next) { this.val = val; this.next = next; }
 * }
 */
class Solution {
    public int[][] spiralMatrix(int m, int n, ListNode head) {
        int[][] ans = new int[m][n];
        int total = m * n;
        int current = 0; 
        int minRow = 0;
        int maxRow = m-1;
        int minCol = 0;
        int maxCol = n-1; 
        while(current < total) {
            // RIGHT
            for(int r = minRow, c = minCol; c <= maxCol; c++) {
                if(head != null) {
                    ans[r][c] = head.val;
                    head = head.next; 
                }
                else {
                    ans[r][c] = -1;
                }
                current++;
            }
            minRow++; 
            if(current >= total) break; 

            // DOWN
            for(int r = minRow, c = maxCol; r <= maxRow; r++) {
                if(head != null) {
                    ans[r][c] = head.val;
                    head = head.next; 
                }
                else {
                    ans[r][c] = -1;
                }
                current++;
            }
            maxCol--; 
            if(current >= total) break; 

            // LEFT
            for(int r = maxRow, c = maxCol; c >= minCol; c--) {
                if(head != null) {
                    ans[r][c] = head.val;
                    head = head.next; 
                }
                else {
                    ans[r][c] = -1;
                }
                current++;
            }
            maxRow--; 
            if(current >= total) break; 



            // UP
            for(int r = maxRow, c = minCol; r >= minRow; r--) {
                if(head != null) {
                    ans[r][c] = head.val;
                    head = head.next; 
                }
                else {
                    ans[r][c] = -1;
                }
                current++;
            }
            minCol++; 

        }


        return ans;
    }
}
