    if (this.heap.length > 0) {
        return this.heap.shift();
    }
    this.min_num += 1;
    return this.min_num - 1;    
};

/** 
 * @param {number} num
 * @return {void}
 */
SmallestInfiniteSet.prototype.addBack = function(num) {
    if (this.min_num > num && this.heap.indexOf(num) === -1) {
        this.heap.push(num);
        this.heap.sort((a, b) => a - b);
    }
};
