class min_heap{
    constructor() {
        this.items = [];
    }
    add(x) {
        let q = this.items;
        q.push(x);
        let idx = q.length - 1;
        while(idx > 0) {
            let p = Math.floor((idx -1)/2);
            if(q[idx] < q[p]) {
                let t = q[idx];
                q[idx] = q[p];
                q[p] = t;
                idx = p;
            } else break
        }
    }
    remove() {
        let q = this.items;
        if(q.length === 1) {
            return q.pop();
        }
        let res = q[0];
        q[0] = q.pop();
        let idx = 0;
        let len = q.length;
        while(true) {
            let l = 2*idx + 1;
            let r = 2*idx + 2;
            let s = null;
            if(l < len && q[l] < q[idx]) {
                s = l;
            }
            if(r < len) {
                if(s === null && q[r] < q[idx]) {
                    s = r;
                }
                if(s !== null && q[r] < q[s]) {
                    s = r;
                }
            }
            if(s !== null) {
                let t = q[idx];
                q[idx] = q[s];
                q[s] = t;
                idx = s;
            } else break;
        }
        return res
    }
}

var SmallestInfiniteSet = function() {
    this.min_h = new min_heap();
    this.set = new Set();
    this.min_value = 1;
};

/**
 * @return {number}
 */
SmallestInfiniteSet.prototype.popSmallest = function() {
    if (this.min_h.items.length === 0) {
        const result = this.min_value
        this.min_value += 1
        return result
    } else {
        const result = this.min_h.remove();
        this.set.delete(result)
        return result
    }
};

/** 
 * @param {number} num
 * @return {void}
 */
SmallestInfiniteSet.prototype.addBack = function(num) {
    if (num < this.min_value && !this.set.has(num)) {
        this.min_h.add(num)
        this.set.add(num)
    }
};

