class SmallestInfiniteSet {
    var popped = Array(repeating: false, count:1002) 
    var smallest = 1

    func popSmallest() -> Int {
        popped[smallest] = true
        defer { smallest = popped[smallest...].firstIndex { !$0 }! } // !! this sets `smallest` AFTER the return statement
        return smallest
    }

    func addBack(_ num: Int) {
        popped[num] = false
        smallest = min(smallest, num)
    }
}
```

