class SmallestInfiniteSet {

    //Hashmap stores the missing values and smallest stores the smallest element present in the set

    HashMap<Integer,Integer> hm;
    int smallest;
    public SmallestInfiniteSet() {
        hm = new HashMap<>();
        smallest = 1;
    }

    public int popSmallest() {
        hm.put(smallest,1);
        int temp = smallest;
        int ans = temp;

        while(hm.containsKey(temp))
            temp+=1;
        smallest = temp;
        return ans;
    }

    public void addBack(int num) {
        if(hm.containsKey(num))
            hm.remove(num);
        smallest = num<smallest?num:smallest;
    }
}


