class SmallestInfiniteSet {
    HashSet<Integer> hs;
    public SmallestInfiniteSet() {
        hs=new HashSet<>();
    }

    public int popSmallest() {
        for(int i=1;;i++){
            if(!hs.contains(i)){
                hs.add(i);
                return i;
            }
        }
    }

    public void addBack(int num) {
        if(hs.contains(num))
            hs.remove(num);
