class Solution {
    public boolean digitCount(String num) {
        int[] freqArr = new int[10];  // n = 10 given in constraints;


        for(char ch : num.toCharArray()){
            freqArr[ch-'0']++;
        }

        for(int i=0;i<num.length();i++){
