class Solution {
    public boolean digitCount(String num) {
        char[] numx = num.toCharArray();
        int freq = 0;
        for(int i=0; i<numx.length; i++){
            freq = 0;
            for(int j=0; j<numx.length; j++){
                if(i == (numx[j]-'0')){
                    freq++;
                }
            }
            if((numx[i]-'0') != freq) return false;
        }
        return true;
    }
}
