use std::collections::HashMap;

impl Solution {
    pub fn digit_count(num: String) -> bool {
        let mut dc = HashMap::new();
        for c in num.chars() {
            let d: usize = c.to_digit(10).unwrap_or(88) as usize;
            *dc.entry(d).or_insert(0) += 1;
        }

        for (i, c) in num.chars().enumerate() {
            let cnt: usize = c.to_digit(10).unwrap_or(99) as usize;
            if !dc.contains_key(&i) {
                if cnt == 0 {
                    continue
                } else {
                    return false;
                }
            } else if dc[&i] != cnt {
                return false;
            }
        }
        true
    }
}
