class Solution {
public:
    int binarySearch(int num, vector<int>& potions, long long success){
        int left=0, right=potions.size()-1;
        double curr=(double)success/num; 
        while(left<right){
            int mid=left+(right-left)/2;
            if(potions[mid]>=curr){ 
                right=mid;  // set right to mid to search for lower indices
            }
            else left=mid+1;  // else set left to mid+1 to search for higher indices
        }
        if(potions[right]>=curr) return right;  // if any element has product greater than or equal to success
        else return -1;  // else potion with success probability not fount
    }
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        vector<int>ans;
        int n=potions.size();
        sort(potions.begin(),potions.end()); 
        for(int num:spells){
            int idx=binarySearch(num,potions,success);  // find index where condition is satisfied for the first time
            if(idx==-1) ans.push_back(0);  // if potion with success probability not found, add 0
            else  ans.push_back(n-idx);  // else, n-idx would be the number of pairs that satisfy the condition
        }
        return ans;  
    }
};

```

