        const pairs = [];
        potions.sort((a, b) => a - b);
        const spellsLength = spells.length;
        const potionsLength = potions.length;

        for (let i = 0; i < spellsLength; i++) {
            let count = 0;
            // binary search
            let left = 0;
            let right = potionsLength - 1;
            while (left <= right) {
                // const mid = Math.floor((left + right) / 2);
                const mid = (left + right) >> 1;
