        vector<int> potionsCount(max + 1, 0);
        for (int x : potions)
            potionsCount[x]++;

        int count = 0;
        for (int i = max; i >= 0; i--) {
            count += potionsCount[i];
            potionsCount[i] = count;
        }

        vector<int> ret(n, 0);
        for (int i = 0; i < n; i++) {
            long long target = success / spells[i];
            if (target > max)
                continue;

            while (target < 100001 && target * spells[i] < success)
                target++;

            if (target > max)
                continue;

            ret[i] = potionsCount[target];
        }

        return ret;
    }
};
