class Solution {
public:
    int solve(vector<int>& p, int i, long long success){
        long l = 0, h = p.size()-1;

        while(l <= h){
            long mid = l + (h-l)/2;

            if((long long)p[mid]*i >= success){
                h = mid-1;
            }
            else{
                l = mid+1;
            }
        }

        return p.size() - l;
    }

    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        sort(potions.begin(), potions.end());
        vector<int> ans;

        for(int i : spells){
            int count = solve(potions, i, success);

            if(count < 0)
                ans.push_back(0);
            else
                ans.push_back(count);
        }

        return ans;
    }
};
