# Code
```
class Solution {
public:
    vector<int> successfulPairs(vector<int>& spells, vector<int>& potions, long long success) {
        vector<int> ans;
        int n=potions.size();
        sort(potions.begin(),potions.end());
        for(int i=0;i<spells.size();i++){
            long long req = ceil(1.0 * success/spells[i]);
            if(req>potions[potions.size()-1]){
                ans.push_back(0);
                continue;
            }
            auto idx= lower_bound(potions.begin(),potions.end(),req) - potions.begin();
            ans.push_back(n-idx);
        }
        return ans;
    }
};
