           int low = 0;
           int high = m-1;
           int cnt =0;
           while(low<=high){
               long long mid = low+(high-low)/2;
               if((long)spells[i]*(long)potions[mid]>=success){
                    cnt += high-mid+1;
                    high = mid-1;
               }
               else if((long)spells[i]*(long)potions[mid]<success){
                   low = mid+1;
               }
           }
          pairs.push_back(cnt);
        }
        return pairs;
    }
};
