        vector<int> ans;
        int maxPotionValue=potions[m-1];
        for(int i=0;i<n;i++) // O(n*logm)
        {
            int spell=spells[i];
            // minPotion*spell>=success
            long long minPotion=ceil((1.0*success)/spell);
            if(minPotion>maxPotionValue)
            {
                ans.push_back(0);
                continue;
            }
            int index=LowerBound(0,m-1,potions,minPotion);
            int count=m-index;
            ans.push_back(count);
        }
        return ans;
    }
};

