class Solution:
    def sellingWood(self, m: int, n: int, prices: List[List[int]]) -> int:

        priceMap = {}
        for p in prices:
            if p[0] in priceMap:
                priceMap[p[0]][p[1]] = p[2]
            else:
                priceMap[p[0]] = {p[1]: p[2]}

        def getPrice(h, w):
            if h in priceMap and w in priceMap[h]:
                return priceMap[h][w]
            return 0

        @cache
        def rec(h, w):
            money = getPrice(h, w)

            # cut horizontal
            for i in range(1, h):
                money = max(money, rec(i, w) + rec(h - i, w))

            # cut vertical
            for i in range(1, w):
                money = max(money, rec(h, i) + rec(h, w - i))

            return money

        return rec(m, n)

