import java.util.*;

class Solution {

    public long helper(long[][] max, int m, int n, Long[][] ddp) {
        if (ddp[m][n] != null) return ddp[m][n];
        ddp[m][n] = max[m][n];
        for (int i = 1; i < m; i++) {
            ddp[m][n] = Math.max(ddp[m][n], helper(max, i, n, ddp) + helper(max, m-i, n, ddp));
        }
        for (int i = 1; i < n; i++) {
            ddp[m][n] = Math.max(ddp[m][n], helper(max, m, i, ddp) + helper(max, m, n-i, ddp));
        }
        return ddp[m][n];
    }

    public long sellingWood(int m, int n, int[][] prices) {
        long[][] max = new long[m+3][n+1];
        Arrays.sort(prices, (a,b)->(a[0]-b[0]));
        long[] dp = new long[n+1];
        int k = 0;
        for (int i = 1; i <= m; i++) {
            while (k < prices.length && prices[k][0] <= i) {
                for (int p = prices[k][1]; p <= n; p++) {
                    dp[p] = Math.max(dp[p], dp[p-prices[k][1]]+prices[k][2]);
                }
                k++;
            }
            for (int j = 0; j <= n; j++) max[i][j] = dp[j];
        }

        Long[][] ddp = new Long[m+3][n+1];

        return helper(max, m, n, ddp);
    }
}
