class Solution {
    public long sellingWood(int m, int n, int[][] prices) {
        Long[][] dp = new Long[m + 1][n + 1];
        dp[0][0] = 0L;
        HashMap<Integer, HashMap<Integer, Long>> heightToWidthToPrice = new HashMap<Integer, HashMap<Integer, Long>>();
        for(int[] price : prices) {
            HashMap<Integer, Long> widthToPrice = heightToWidthToPrice.get(price[0]);
            if(widthToPrice == null) {
                widthToPrice = new HashMap<>();
                heightToWidthToPrice.put(price[0], widthToPrice);
            }

            widthToPrice.put(price[1], (long)price[2]);
        }

        return dfs(dp, m, n, heightToWidthToPrice);
    }

    private long dfs(Long[][] dp, int m, int n, HashMap<Integer, HashMap<Integer, Long>> heightToWidthToPrice) {
        if(dp[m][n] != null) {
            return dp[m][n];
        }

        dp[m][n] = 0L;
        HashMap<Integer, Long> widthToPrice = heightToWidthToPrice.get(m);
        if(widthToPrice != null) {
            dp[m][n] = widthToPrice.getOrDefault(n, 0L);
        }

        long candidate  = 0;
        for(int i = 1; i <m; ++i) {
            candidate = dfs(dp, i, n, heightToWidthToPrice) + 
                dfs(dp, m-i, n, heightToWidthToPrice);
            dp[m][n] = Math.max(dp[m][n], candidate);
        }

        for(int j = 1; j < n; ++j) {
            candidate = dfs(dp, m, j, heightToWidthToPrice) + 
                dfs(dp, m, n-j, heightToWidthToPrice);
            dp[m][n] = Math.max(dp[m][n], candidate);
        }

        return dp[m][n];
    }
}
