/**
 * @param {number} m
 * @param {number} n
 * @param {number[][]} prices
 * @return {number}
 */
var sellingWood = function(m, n, prices) {
    let prObj = {}
    let checked = {}

    for (let i = 0; i < prices.length; i++){
        prObj["" + prices[i][0] + "/" + prices[i][1]] = prices[i][2]
    }

    return iter(m,n)

    function iter(row, col){
        if (checked["" + row + "/" + col] !== undefined) return checked["" + row + "/" + col]
        let p = 0 // max price of current piece.
        if (prObj["" + row + "/" + col] !== undefined) p = prObj["" + row + "/" + col]

        for (let i = 1; i <= Math.floor(row / 2); i++){
            p = Math.max(p, iter(i, col) + iter(row - i, col))
        }

        for (let i = 1; i <= Math.floor(col / 2); i++){
            p = Math.max(p, iter(row, i) + iter(row, col - i))
        }
        checked["" + row + "/" + col] = p
        return p
    }
};
