class Solution {
public:
    ll priceList[201][201];
    ll dp[201][201];
    ll solve(int height,int width){
        //base case
        if(height==0 || width==0)
            return 0;

        //check the cache
        if(dp[height][width]!=-1)
            return dp[height][width];

        ll maxProfit=0;

        // when sell entire plank
        maxProfit = priceList[height][width];

        //when cut height wise
        for(int i=1;i<height;i++){
            ll curr = solve(i,width) + solve(height-i,width);
            maxProfit=max(maxProfit,curr);
        }

        //when cut width wise
        for(int i=1;i<width;i++){
            ll curr = solve(height,i) + solve(height,width-i);
            maxProfit=max(maxProfit,curr);
        }

        return dp[height][width] = maxProfit;
    }
