class Solution:
    def sellingWood(self, m: int, n: int, prices: List[List[int]]) -> int:

        #dimension to price mapping
        price = {}

        for l,b,p in prices:
            price[(l,b)] = p

        #dp[i][j] = money earned by selling ixj dim plank
        dp = [[price[(i,j)] if (i,j) in price else 0 for j in range(n+1)]\
              for i in range(m+1)] 

        #generate empty plank to mxn plank
        for i in range(1,m+1):
            for j in range(1,n+1):

                #cut it horizontally
                for r in range(1,(i//2)+1):
                    dp[i][j] = max(
                        dp[i][j],
                        dp[r][j]+dp[i-r][j]
                    )

                #cut vertically
                for c in range(1,(j//2)+1):
                    dp[i][j] = max(
                        dp[i][j],
                        dp[i][c]+dp[i][j-c]
                    )

        return dp[m][n]
