class Solution {
    public long sellingWood(int m, int n, int[][] prices) {
        long[][] dp = new long[m+1][n+1];

        Map<String, Integer> cost = new HashMap<>();

        for (int[] price : prices) {
            cost.put(price[0] + ":" + price[1], price[2]);
        }

        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                // vertical cut
                for (int v = 1; v <= j/2; v++) {
                    dp[i][j] = Math.max(dp[i][j], dp[i][v] + dp[i][j-v]);
                }
                // horizontal cut
                for (int h = 1; h <= i/2; h++) {
                    dp[i][j] = Math.max(dp[i][j], dp[h][j] + dp[i-h][j]);
                }
                // Cost already given
                dp[i][j] = Math.max(dp[i][j], cost.getOrDefault(i + ":" + j, 0));
            }
        }
        return dp[m][n];
    }
}
